sqoop import --connect jdbc:mysql://localhost/CDW_SAPP --driver com.mysql.jdbc.Driver  -m 1 --query "SELECT SSN, concat(ucase(mid(FIRST_NAME,1,1)), mid(lower(FIRST_NAME),2)) as FIRST_NAME, lower(MIDDLE_NAME) as MIDDLE_NAME, concat(ucase(mid(LAST_NAME,1,1)), mid(lower(LAST_NAME),2)) as LAST_NAME, CREDIT_CARD_NO, concat_ws(' ',APT_NO, STREET_NAME) as CUST_STREET, CUST_CITY, CUST_STATE, CUST_COUNTRY, CUST_ZIP, concat_ws('-',mid(cust_phone,1,3),mid(cust_phone,3)) as CUST_PHONE, CUST_EMAIL, LAST_UPDATED FROM CDW_SAPP_CUSTOMER WHERE \$CONDITIONS" --delete-target-dir --target-dir /Credit_Card_System/CDW_SAPP_D_CUSTOMER
