sqoop job --meta-connect jdbc:hsqldb:hsql://localhost:16000/sqoop --create branch_sqoop_all -- import --connect jdbc:mysql://localhost/CDW_SAPP --driver com.mysql.jdbc.Driver -m 1 --query "SELECT BRANCH_CODE, BRANCH_NAME, BRANCH_STREET, BRANCH_CITY, BRANCH_STATE, ifNULL (BRANCH_ZIP, 999999) BRANCH_ZIP,  concat('(',concat_ws(')',mid(concat_ws('-',mid(BRANCH_PHONE,1,6),mid(BRANCH_PHONE,6)),1,3),mid(concat_ws('-',mid(BRANCH_PHONE,1,6),mid(BRANCH_PHONE,6)),4))) as BRANCH_PHONE, LAST_UPDATED FROM CDW_SAPP_BRANCH WHERE \$CONDITIONS" --delete-target-dir --target-dir /Credit_Card_System/CDW_SAPP_D_BRANCH
